function initInputFile(e){var t=$(e).attr("data-input");console.log(t);var n=$("input[id="+t+"]");$(n).hide();var r='<div class="edit">Change the image</div>';$(e).append(r)}function showPreview(e){var t=$(e).attr("id");if(e.files&&e.files[0]){var n=new FileReader;n.onload=function(e){var n=e.target.result,r=$("#editImage[data-input="+t+"]");$("img",r).remove();$(r).prepend('<img class="newImg" src="'+n+'" alt="preview"/>')};n.readAsDataURL(e.files[0])}}$(document).ready(function(){$("div#editImage").each(function(){initInputFile(this)});$("form").on("click","#editImage",function(){var e=$(this).data("input");$("input[id="+e+"]").click()});$("input[type=file]").change(function(){showPreview(this)})});