function initInputFile(i){var e=$(i).attr("data-input"),t=$("input[id="+e+"]");$(t).hide();var n='<div class="edit">Change the image</div>';$(i).append(n)}function showPreview(i){var e=$(i).attr("id");if(i.files&&i.files[0]){var t=new FileReader;t.onload=function(i){var t=i.target.result,n=$("#editImage[data-input="+e+"]");$("img",n).remove(),$(n).prepend('<img class="newImg" src="'+t+'" alt="preview"/>')},t.readAsDataURL(i.files[0])}}$(document).ready(function(){$("div#editImage").each(function(){initInputFile(this)}),$("form").on("click","#editImage",function(){var i=$(this).data("input");$("input[id="+i+"]").click()}),$("input[type=file]").change(function(){showPreview(this)})});